package App.Java02;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Soit la mise en place du variable int money avec pour n'importe quel valeur
	
	
		int money = 1000;
		// if la variable de condition 
		// Qui dit que si la moneu équivaut à 2000
	
		if(money == 2000) {
			System.out.print("La somme est suffisante pour se payer le téléphone");
			//Le system renvoit le message la somme est okay
			// else pour signifier aussi
			// Si la somme atendue n'est plus celle demandée
			// le second message est reournée
		} else {	
			//Celui-i est le second message retournée
			System.out.println("La somme n'est pas suffisante");
		}

	}

}
